import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from './AuthProvider'; // Make sure to import useAuth
import { auth } from './firebaseConfig'; // Import the auth object from your Firebase config
import './Navbar.css';

function Navbar() {
  const { currentUser } = useAuth(); // Use the useAuth hook to access the current user

  const handleLogout = async () => {
    try {
      await auth.signOut();
    } catch (error) {
      console.error("Error signing out: ", error);
    }
  };

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-brand">A.I.D.E.N</Link>
      <ul className="nav-links">
        {currentUser ? (
          <>
            <li className="nav-item">
              <Link to="/add-task" className="nav-link">Add Task</Link>
            </li>
            <li className="nav-item">Hi, {currentUser.displayName || 'User'}</li>
            <li className="nav-item">
              <button className="logout-btn" onClick={handleLogout}>Logout</button>
            </li>
          </>
        ) : (
          <li className="nav-item">
            <Link to="/login" className="nav-link">Login</Link>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
