import React, { useState, useEffect } from 'react';
import './TaskList.css'; // Ensure you have this CSS file in the same directory

const TaskList = () => {
    const [tasks, setTasks] = useState([]);

    // Simulate fetching tasks from an API
    useEffect(() => {
        const fetchTasks = async () => {
            // Placeholder for fetching tasks from your API
            const fetchedTasks = [
                // Sample tasks, replace with actual API call
                { id: 1, title: 'Task 1', description: 'Description 1', dueDate: '2024-04-30', priority: 'Medium', status: 'Not Started', tags: ['tag1', 'tag2'] },
                { id: 2, title: 'Task 2', description: 'Description 2', dueDate: '2024-05-15', priority: 'High', status: 'In Progress', tags: ['tag2'] },
                // Add more tasks as needed
            ];
            setTasks(fetchedTasks);
        };

        fetchTasks();
    }, []);

    return (
        <div className="task-list-container">
            <h2>Tasks</h2>
            <div className="task-list">
                {tasks.map(task => (
                    <div key={task.id} className="task-item">
                        <h3>{task.title}</h3>
                        <p>{task.description}</p>
                        <p>Due: {task.dueDate}</p>
                        <p>Priority: {task.priority}</p>
                        <p>Status: {task.status}</p>
                        <p>Tags: {task.tags.join(', ')}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TaskList;
