import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from './AuthProvider'; // Assuming you have an AuthProvider component managing auth state
import './Navbar.css';

function Navbar() {
  const { currentUser } = useContext(AuthContext); // Using context to get the current user

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-brand">A.I.D.E.N</Link>
      <ul className="nav-links">
        <li>
          <Link to="/add-task" className="nav-link">Add Task</Link>
        </li>
        {currentUser && (
          <li className="nav-link">Hi, {currentUser.displayName}</li> // Display user's name if logged in
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
