import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from './AuthProvider'; // Make sure to import useAuth
import './Navbar.css';

function Navbar() {
  const { currentUser } = useAuth(); // Use the useAuth hook to access the current user

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-brand">A.I.D.E.N</Link>
      <ul className="nav-links">
        {currentUser ? (
          <>
            <li>
              <Link to="/add-task" className="nav-link">Add Task</Link>
            </li>
            <li className="nav-link">Hi, {currentUser.displayName || 'User'}</li> {/* Display user's name if logged in */}
          </>
        ) : (
          <li>
            <Link to="/login" className="nav-link">Login</Link>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
