import React, { useState } from 'react';
import { db } from './firebaseConfig'; // Ensure this path is correct
import { collection, addDoc } from "firebase/firestore"; 
import './AddTaskForm.css';

const AddTaskForm = () => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [dueDate, setDueDate] = useState('');
    const [priority, setPriority] = useState('Medium');
    const [status, setStatus] = useState('Not Started');
    const [tags, setTags] = useState('');
    const [subtasks, setSubtasks] = useState(''); // New state for subtasks

    const handleSubmit = async (e) => {
        e.preventDefault();
        const taskTags = tags.split(',').map(tag => tag.trim());
        const taskSubtasks = subtasks.split(';').map(subtask => {
            const [title, completed] = subtask.split(',').map(part => part.trim());
            return { title, completed: completed === 'true' };
        });
        try {
            await addDoc(collection(db, "tasks"), {
                title,
                description,
                dueDate,
                priority,
                status,
                tags: taskTags,
                subtasks: taskSubtasks, // Include subtasks in the document
            });
            console.log("Task added!");
        } catch (error) {
            console.error("Error adding task: ", error);
        }
        // Reset form fields
        setTitle('');
        setDescription('');
        setDueDate('');
        setPriority('Medium');
        setStatus('Not Started');
        setTags('');
        setSubtasks(''); // Reset subtasks field
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit} className="task-form">
                <h2>Add a New Task</h2>
                {/* Existing form fields */}
                <div className="form-group">
                    <label htmlFor="subtasks">Subtasks (Format: title,completed; title,completed; ...):</label>
                    <input
                        type="text"
                        id="subtasks"
                        value={subtasks}
                        onChange={(e) => setSubtasks(e.target.value)}
                        placeholder="Subtask 1,false; Subtask 2,true;"
                    />
                </div>
                <button type="submit">Add Task</button>
            </form>
        </div>
    );
};

export default AddTaskForm;
